<script>
export default {
  name: "HeroCommon",
  data() {
    return {
      pdfLink: require("../../assets/images/documents/resume.pdf").default
    };
  },
  mounted() {
    function TextAnimate(path, pathLength) {
      this.path = path;
      this.pathLength = pathLength;
      this.speed = 3;
      path.style.strokeDasharray = this.pathLength;
      path.style.strokeDashoffset = this.pathLength;
    }

    TextAnimate.prototype.drawText = function() {
      this.path.style.strokeDashoffset -= this.speed;
      this.path.style.strokeDashoffset = Math.max(
        0,
        this.path.style.strokeDashoffset
      );
    };

    function drawLetter(path) {
      function animate() {
        path.path.style.strokeDashoffset -= path.speed;
        path.path.style.strokeDashoffset = Math.max(
          0,
          path.path.style.strokeDashoffset
        );
        window.requestAnimationFrame(animate);
      }

      animate();
    }

    const paths = document.querySelectorAll("#front-end path");
    let pathsArr = [];

    for (let i = 0; i < paths.length; i++) {
      pathsArr.push(new TextAnimate(paths[i], paths[i].getTotalLength()));
    }
    let time = 0;
    pathsArr.forEach(path => {
      setTimeout(function() {
        drawLetter(path);
      }, time);
      time += 200;
    });
  }
};
</script>
